////////////////////////////////////////////////////////////////////////////////////////////////////
// Types for importing via Webpack
////////////////////////////////////////////////////////////////////////////////////////////////////
declare module '*.txt' {
    const value: string;
    export default value;
}

declare module '*.svg' {
    const value: string;
    export default value;
}

////////////////////////////////////////////////////////////////////////////////////////////////////
// Globals
////////////////////////////////////////////////////////////////////////////////////////////////////
declare module '@photon-rush/globalTypes' {
    export interface ITag {
        name  : string,
        hash  : string,
        branch: string,
        date  : string,
        mode  : string,
    }
}

declare module '@photon-rush/tag' {
    import { ITag } from '@photon-rush/globalTypes';

    const value: ITag;

    export default value;
}